package lb2

import (
	"testing"
)

func TestTask5Lab2(t *testing.T) {
	tests := []struct {
		name           string;
    base int
		power         int 
		expectedResult string
	}{
		{
			name:   "Test 1",
      base: 7,
			power:  123,
      // 8.852357e+103 from calculator 
      expectedResult: "88523570369346801684435811372718127585670061114702144933569245260093253728999880981421881473709365496343",
		},
		{
			name:   "Test 2",
      base: 2,
			power:  6,
      expectedResult: "64",
		},
		{
			name:   "Test 3",
      base: 7,
			power:  2000,
      // Just believe
      expectedResult: "",
		},
		// {
		// 	name:   "Test 2 with big value",
		// 	power:  big.NewInt(10_000_000),
		// },
	}

	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			result := Lab2Task5(test.base, test.power)

			if (result != test.expectedResult) {
				t.Errorf("Lab2Task5 %v failed", test.name)
			}
		})
	}
}
